import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";

const Expenses = (props) => {
  const expenses = props.items;
  const [selectedFilter, setSelectedFilter] = useState("");

  const applyFilterHandler = (filter) => {
    setSelectedFilter(() => {
      return filter;
    });
    console.log("Inside Expenses.js : ", selectedFilter);
  };

  return (
    <Card className="expenses">
      <ExpensesFilter
        className="expenses"
        onFilterChanged={applyFilterHandler}
      />
      {expenses.map((expense) => {
        let year =  new Date(expense.date).getFullYear;
        console.log('Expense Year : ', year, '\nFilter : ', sele)
        if (
          year.toString() === parseInt(selectedFilter) ||
          selectedFilter.trim().length === 0
        ) {
          return (
            <ExpenseItem
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          );
        } else {
          return "";
        }
      })}
    </Card>
  );
};

export default Expenses;
