import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";

const Expenses = (props) => {
  const expenses = props.items;
  const [selectedFilter, setSelectedFilter] = useState("");

  const applyFilterHandler = (filter) => {
    setSelectedFilter(() => {
      return filter;
    });
    console.log("Inside Expenses.js : ", selectedFilter);
  };

  const filteredExpenses = expenses.filter((expense) => {
    return (
      new Date(expense.date).getFullYear().toString() === selectedFilter ||
      selectedFilter.trim().length === 0
    );
  });

  const expensesContent = <p>No expense is found.</p>;

  if(filteredExpenses.length > 0) {
    expensesContent = filteredExpenses.map((expense) => {
      return (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      );
    })
  }
