import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";

const Expenses = (props) => {
  const expenses = props.items;
  const [selectedFilter, setSelectedFilter] = useState("");

  const applyFilterHandler = (filter) => {
    setSelectedFilter(() => {
      return filter;
    });
    console.log("Inside Expenses.js : ", selectedFilter);
  };

  const filteredExpenses = (expenses) => {
    expenses.filter((expense) => {
      return (
        new Date(expense.date).getFullYear() === selectedFilter ||
        selectedFilter.trim().length === 0
      );
    });
  };

  // let year = new Date(expense.date).getFullYear();
  //       if (
  //         year.toString() === selectedFilter ||
  //         selectedFilter.trim().length === 0
  //       ) {
  //         return (
  //           <ExpenseItem
  //             key={expense.id}
  //             title={expense.title}
  //             amount={expense.amount}
  //             date={expense.date}
  //           />
  //         );
  //       } else {
  //         return "";
  //       }

  return (
    <Card className="expenses">
      <ExpensesFilter
        className="expenses"
        onFilterChanged={applyFilterHandler}
      />
      {expenses.map((expense) => {
        let year = new Date(expense.date).getFullYear();
        if (
          year.toString() === selectedFilter ||
          selectedFilter.trim().length === 0
        ) {
          return (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          );
        } else {
          return "";
        }
      })}
    </Card>
  );
};

export default Expenses;
