import "./Chart.css";
import ChartBar from "./ChartBar";

const Chart = (props) => {
    
  const fillBar = (dataPoint) => {
    const monthsFound = props.items.filter((item) => {
      let month = new Date(item.date).getMonth();
      return month.toString() === dataPoint.toString();
    });

    let month = new Date().toLocaleString('month', dataPoint);
    return <ChartBar label={dataPoint} key={dataPoint} value={dataPoint} fill={monthsFound.length}/>
  };

  return parseInt(props.dataPoints).map((dataPoint) => {
    return fillBar(dataPoint);
  });
};

export default Chart;
