import "./NewExpense.css";
import React from "react";
import ExpenseForm from "./ExpenseForm";
import Card from "../UI/Card";

const NewExpense = (props) => {

  const saveExpenseHandler = (expense) => {
    const newExpense = { ...expense };
    console.log("Inside New Expense : ", newExpense);
    props.onAddExpense(newExpense);
  };

  let newExpenseContent = false;

  const formShowHandler = (e) => {
    if(e.target.value === true) {
      newExpenseContent = true;
    }else{
      newExpenseContent = false;
    }
  };

  return (
    <Card>
      <Card className="new-expense">
        (newExpenseContent)
        <ExpenseForm onSaveExpenseData={saveExpenseHandler} onShowExpenseForm={formShowHandler}/>
      </Card>
    </Card>
    
  );
};

export default NewExpense;
