import "./ExpenseItem.css";

function ExpenseItem(props) {
  // const expenseName = "Health Insurance";
  // const expensePrice = 10.0;
  // const expenseDate = new Date();

  return (
    <div className="expense-item">
      <div className="expense-item__date">
        <div className="expense-item__date-day">{props.date.toLocaleString('en-US', {date: 'long'})}</div>
        <div className="expense-item__date-month">{props.date.toLocaleString('en-US', {month: 'long'})}</div>
        <div className="expense-item__date-year">{props.date.toLocaleString('en-US', {year})}</div>
      </div>
      <div className="expense-item__description">
        <h4>{props.title}</h4>
        <div className="expense-item__price">
          ${props.amount.toPrecision(2)}
        </div>
      </div>
    </div>
  );
}

export default ExpenseItem;
